mapper.select:
SELECT 

 FROM bas_material WHERE 1=1 


  ORDER BY ${orderByField}
  
    ${orderBy}

select
from bas_material a LEFT JOIN bas_category b ON a.category_no=b.category_no LEFT JOIN bas_unit c ON a.normal_unit=c.unit_no where 1=1
and material_no = #{materialNo,jdbcType=VARCHAR} or material_code=#{materialNo,jdbcType=VARCHAR}
and brand_no = #{brandNo}
and material_type in(1,2)
and material_type =3

select c.size_name,c.size_no,c.size_type_no from bas_material a INNER JOIN bas_material_size b on a.material_no=b.material_no INNER JOIN bas_size c on a.size_type_no=c.size_type_no and b.size_no=c.size_no LEFT JOIN bas_category d On a.category_no=d.category_no where 1=1
and a.material_no =#{materialNo,jdbcType=VARCHAR}
and c.size_name =#{sizeName,jdbcType=VARCHAR}
